import React, { useState } from 'react';
import { AppBar, Toolbar, Button, Box, Container, IconButton, Drawer, List, ListItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [mobileOpen, setMobileOpen] = useState(false);

  const menuItems = [
    { text: 'Client Gallery', link: '/gallery' },
    { text: 'Website', link: '/website' },
    { text: 'Studio Manager', link: '/studio' },
    { text: 'Store', link: '/store' },
    { text: 'Mobile App', link: '/mobile' },
  ];

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  return (
    <AppBar position="sticky" color="transparent" elevation={0} sx={{ bgcolor: 'white' }}>
      <Container maxWidth="lg">
        <Toolbar sx={{ justifyContent: 'space-between' }}>
          <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
            <Box component="img" src="/logo.png" alt="Pixieset" sx={{ height: 32 }} />
          </Link>

          {/* Desktop Menu */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 2 }}>
            {menuItems.map((item) => (
              <Button
                key={item.text}
                component={Link}
                to={item.link}
                color="primary"
                sx={{ textTransform: 'none' }}
              >
                {item.text}
              </Button>
            ))}
            <Button
              variant="contained"
              color="primary"
              sx={{
                textTransform: 'none',
                borderRadius: 2,
                ml: 2
              }}
            >
              Get Started
            </Button>
          </Box>

          {/* Mobile Menu Icon */}
          <IconButton
            color="primary"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ display: { md: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
        </Toolbar>
      </Container>

      {/* Mobile Drawer */}
      <Drawer
        variant="temporary"
        anchor="right"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        sx={{ display: { xs: 'block', md: 'none' } }}
      >
        <List sx={{ width: 250 }}>
          {menuItems.map((item) => (
            <ListItem key={item.text}>
              <Button
                fullWidth
                component={Link}
                to={item.link}
                onClick={handleDrawerToggle}
                sx={{ textTransform: 'none' }}
              >
                {item.text}
              </Button>
            </ListItem>
          ))}
          <ListItem>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              sx={{ textTransform: 'none', borderRadius: 2 }}
            >
              Get Started
            </Button>
          </ListItem>
        </List>
      </Drawer>
    </AppBar>
  );
};

export default Navbar;
