import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  IconButton,
  Dialog,
  DialogContent,
  Button,
  CircularProgress,
  ImageList,
  ImageListItem,
  Snackbar,
  Alert,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CloseIcon from '@mui/icons-material/Close';
import DownloadIcon from '@mui/icons-material/Download';
import ShareIcon from '@mui/icons-material/Share';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

const GalleryView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [gallery, setGallery] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success',
  });

  useEffect(() => {
    // Simulate fetching gallery data
    const fetchGallery = async () => {
      try {
        // Here you would typically fetch from your API
        const mockGallery = {
          id,
          title: 'Sample Gallery',
          date: '2025-03-20',
          images: Array.from({ length: 20 }, (_, i) => ({
            id: i + 1,
            url: `https://source.unsplash.com/800x600/?wedding,${i}`,
            liked: false,
            description: `Image ${i + 1}`,
          })),
        };
        setGallery(mockGallery);
      } catch (error) {
        console.error('Error fetching gallery:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchGallery();
  }, [id]);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleClose = () => {
    setSelectedImage(null);
  };

  const handleLike = (imageId) => {
    setGallery((prev) => ({
      ...prev,
      images: prev.images.map((img) =>
        img.id === imageId ? { ...img, liked: !img.liked } : img
      ),
    }));
  };

  const handleDownload = (image) => {
    // Here you would typically handle the download
    setSnackbar({
      open: true,
      message: 'Download started',
      severity: 'success',
    });
  };

  const handleShare = (image) => {
    // Here you would typically handle sharing
    navigator.clipboard.writeText(image.url);
    setSnackbar({
      open: true,
      message: 'Link copied to clipboard',
      severity: 'success',
    });
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '60vh',
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ py: 4 }}>
      <Container maxWidth="lg">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Box sx={{ mb: 4 }}>
            <Button
              startIcon={<ArrowBackIcon />}
              onClick={() => navigate('/gallery')}
              sx={{ mb: 2 }}
            >
              Back to Galleries
            </Button>
            <Typography variant="h3" component="h1" gutterBottom>
              {gallery.title}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              {new Date(gallery.date).toLocaleDateString()}
            </Typography>
          </Box>

          <ImageList
            variant="masonry"
            cols={3}
            gap={16}
            sx={{ mb: 4 }}
          >
            {gallery.images.map((image) => (
              <ImageListItem
                key={image.id}
                sx={{
                  cursor: 'pointer',
                  overflow: 'hidden',
                  borderRadius: 2,
                }}
              >
                <motion.div
                  whileHover={{ scale: 1.03 }}
                  transition={{ duration: 0.3 }}
                >
                  <Box
                    component="img"
                    src={image.url}
                    alt={image.description}
                    onClick={() => handleImageClick(image)}
                    sx={{
                      width: '100%',
                      height: 'auto',
                      display: 'block',
                    }}
                  />
                </motion.div>
              </ImageListItem>
            ))}
          </ImageList>
        </motion.div>
      </Container>

      {/* Image Viewer Dialog */}
      <Dialog
        open={!!selectedImage}
        onClose={handleClose}
        maxWidth="xl"
        fullWidth
      >
        <DialogContent sx={{ p: 0, position: 'relative' }}>
          <IconButton
            onClick={handleClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              bgcolor: 'rgba(0, 0, 0, 0.5)',
              color: 'white',
              '&:hover': { bgcolor: 'rgba(0, 0, 0, 0.7)' },
            }}
          >
            <CloseIcon />
          </IconButton>
          {selectedImage && (
            <Box sx={{ position: 'relative' }}>
              <Box
                component="img"
                src={selectedImage.url}
                alt={selectedImage.description}
                sx={{
                  width: '100%',
                  height: 'auto',
                  display: 'block',
                }}
              />
              <Box
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  bgcolor: 'rgba(0, 0, 0, 0.5)',
                  color: 'white',
                  p: 2,
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                }}
              >
                <Typography variant="body1">
                  {selectedImage.description}
                </Typography>
                <Box>
                  <IconButton
                    color="inherit"
                    onClick={() => handleLike(selectedImage.id)}
                  >
                    {selectedImage.liked ? (
                      <FavoriteIcon sx={{ color: '#ff1744' }} />
                    ) : (
                      <FavoriteBorderIcon />
                    )}
                  </IconButton>
                  <IconButton
                    color="inherit"
                    onClick={() => handleDownload(selectedImage)}
                  >
                    <DownloadIcon />
                  </IconButton>
                  <IconButton
                    color="inherit"
                    onClick={() => handleShare(selectedImage)}
                  >
                    <ShareIcon />
                  </IconButton>
                </Box>
              </Box>
            </Box>
          )}
        </DialogContent>
      </Dialog>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default GalleryView;
