import React, { useState, useCallback } from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  IconButton,
  LinearProgress,
  Snackbar,
  Alert
} from '@mui/material';
import { motion } from 'framer-motion';
import AddIcon from '@mui/icons-material/Add';
import CloseIcon from '@mui/icons-material/Close';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { useDropzone } from 'react-dropzone';

const Gallery = () => {
  const [galleries, setGalleries] = useState([
    {
      id: 1,
      title: 'Sarah & John Wedding',
      date: '2025-03-15',
      coverImage: 'https://source.unsplash.com/800x600/?wedding',
      imageCount: 245
    },
    {
      id: 2,
      title: 'Mountain Landscape',
      date: '2025-03-10',
      coverImage: 'https://source.unsplash.com/800x600/?landscape,mountain',
      imageCount: 124
    },
    {
      id: 3,
      title: 'Corporate Event',
      date: '2025-03-05',
      coverImage: 'https://source.unsplash.com/800x600/?corporate,event',
      imageCount: 89
    }
  ]);

  const [openDialog, setOpenDialog] = useState(false);
  const [newGallery, setNewGallery] = useState({
    title: '',
    date: new Date().toISOString().split('T')[0]
  });
  const [uploadProgress, setUploadProgress] = useState(0);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });

  const onDrop = useCallback((acceptedFiles) => {
    // Simulate file upload progress
    let progress = 0;
    const interval = setInterval(() => {
      progress += 10;
      setUploadProgress(progress);
      if (progress >= 100) {
        clearInterval(interval);
        setSnackbar({
          open: true,
          message: 'Images uploaded successfully!',
          severity: 'success'
        });
        // Here you would typically send the files to your backend
        console.log('Uploaded files:', acceptedFiles);
      }
    }, 500);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png']
    }
  });

  const handleCreateGallery = (e) => {
    e.preventDefault();
    const newId = Math.max(...galleries.map(g => g.id)) + 1;
    const gallery = {
      id: newId,
      title: newGallery.title,
      date: newGallery.date,
      coverImage: 'https://source.unsplash.com/800x600/?photography',
      imageCount: 0
    };
    setGalleries([gallery, ...galleries]);
    setOpenDialog(false);
    setNewGallery({ title: '', date: new Date().toISOString().split('T')[0] });
    setSnackbar({
      open: true,
      message: 'Gallery created successfully!',
      severity: 'success'
    });
  };

  return (
    <Box sx={{ py: 8 }}>
      <Container maxWidth="lg">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 6 }}>
            <Typography variant="h3" component="h1" sx={{ fontWeight: 700 }}>
              Client Galleries
            </Typography>
            <Button
              variant="contained"
              color="primary"
              startIcon={<AddIcon />}
              onClick={() => setOpenDialog(true)}
              sx={{
                px: 3,
                py: 1.5,
                '&:hover': {
                  transform: 'translateY(-2px)',
                  transition: 'transform 0.2s ease-in-out'
                }
              }}
            >
              Create New Gallery
            </Button>
          </Box>

          <Grid container spacing={4}>
            {galleries.map((gallery, index) => (
              <Grid item xs={12} sm={6} md={4} key={gallery.id}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.8, delay: index * 0.1 }}
                >
                  <Card
                    sx={{
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column',
                      '&:hover': {
                        transform: 'translateY(-8px)',
                        transition: 'transform 0.3s ease-in-out'
                      }
                    }}
                  >
                    <CardMedia
                      component="img"
                      height="200"
                      image={gallery.coverImage}
                      alt={gallery.title}
                    />
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        {gallery.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Date: {new Date(gallery.date).toLocaleDateString()}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {gallery.imageCount} images
                      </Typography>
                      <Button
                        variant="outlined"
                        color="primary"
                        fullWidth
                        sx={{ mt: 2 }}
                      >
                        View Gallery
                      </Button>
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))}
          </Grid>
        </motion.div>
      </Container>

      {/* Create Gallery Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>
          Create New Gallery
          <IconButton
            onClick={() => setOpenDialog(false)}
            sx={{ position: 'absolute', right: 8, top: 8 }}
          >
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handleCreateGallery} sx={{ mt: 2 }}>
            <TextField
              fullWidth
              label="Gallery Title"
              value={newGallery.title}
              onChange={(e) => setNewGallery({ ...newGallery, title: e.target.value })}
              required
              sx={{ mb: 3 }}
            />
            <TextField
              fullWidth
              type="date"
              label="Event Date"
              value={newGallery.date}
              onChange={(e) => setNewGallery({ ...newGallery, date: e.target.value })}
              required
              sx={{ mb: 3 }}
              InputLabelProps={{ shrink: true }}
            />
            <Box
              {...getRootProps()}
              sx={{
                p: 3,
                border: '2px dashed',
                borderColor: isDragActive ? 'primary.main' : 'grey.300',
                borderRadius: 2,
                textAlign: 'center',
                cursor: 'pointer',
                mb: 3
              }}
            >
              <input {...getInputProps()} />
              <CloudUploadIcon sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
              <Typography>
                {isDragActive
                  ? 'Drop the images here'
                  : 'Drag & drop images here, or click to select files'}
              </Typography>
            </Box>
            {uploadProgress > 0 && (
              <Box sx={{ mb: 3 }}>
                <LinearProgress variant="determinate" value={uploadProgress} />
                <Typography variant="body2" color="text.secondary" align="right">
                  {uploadProgress}%
                </Typography>
              </Box>
            )}
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              size="large"
            >
              Create Gallery
            </Button>
          </Box>
        </DialogContent>
      </Dialog>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Gallery;
